CREATE PROCEDURE [dbo].[UpdateTableDateTime] 
AS 
BEGIN 

DECLARE @Sql NVARCHAR(MAX);
DECLARE @ColumnName NVARCHAR(MAX);
DECLARE @TableName Varchar(50);
DECLARE @TableSchema Varchar(50);
DECLARE @TargetColumnName Varchar(50);
DECLARE @ColumnDefaultValue nvarchar(1000)
DECLARE @RemoveConstraint bit;

SET @RemoveConstraint = 0;
SET nocount on;
DECLARE @trancount INT;

DECLARE @temp TABLE
    (
	TableSchema varchar(50),
    TableName varchar(50),
    TargetColumnName varchar(50),
	ColumnDefaultValue nvarchar(1000)
    )

--get all datetime2 columns in all tables of DB
INSERT INTO @temp SELECT TABLE_SCHEMA, TABLE_NAME, column_name, column_default
FROM information_schema.columns where data_type = 'datetime2' and
TABLE_NAME in (SELECT TABLE_NAME FROM INFORMATION_SCHEMA.TABLES where TABLE_TYPE = 'BASE TABLE');

DECLARE cur CURSOR FOR SELECT TableSchema, TableName, TargetColumnName, ColumnDefaultValue FROM @temp
OPEN cur

FETCH NEXT FROM cur INTO @TableSchema, @TableName, @TargetColumnName, @ColumnDefaultValue

WHILE @@FETCH_STATUS = 0 BEGIN

	--Check does default constraint exist
	IF EXISTS (SELECT *   FROM sys.default_constraints
   WHERE object_id = OBJECT_ID(N'DF_'+@TableName+'_'+@TargetColumnName)
   AND parent_object_id = OBJECT_ID(+ @TableSchema + '.' + @TableName))
	BEGIN
		SET @RemoveConstraint = 1;
	END

		--If default constraint exists, remove it
		IF (@ColumnDefaultValue = '(getdate())' and @RemoveConstraint = 1) BEGIN
			SET @Sql = N'ALTER TABLE ' + @TableSchema + '.' + @TableName + N' DROP CONSTRAINT DF_'+@TableName+'_'+@TargetColumnName+';'
			EXECUTE sp_executesql @Sql
		END

		--Change type of original column
		SET @Sql = N'ALTER TABLE ' + @TableSchema + '.' + @TableName + N' ALTER COLUMN '+@TargetColumnName+' DATETIMEOFFSET;'
		EXECUTE sp_executesql @Sql

		--Copy data from temp column
		SET @Sql = N'UPDATE ' + @TableSchema + '.' + @TableName + N' set '+@TargetColumnName+' = [dbo].ConvertToDateTimeOffset('+@TargetColumnName+');'
		EXECUTE sp_executesql @Sql

		--Add default value constraint, if it was before
		IF (@ColumnDefaultValue = '(getdate())' and @RemoveConstraint = 1) BEGIN
			SET @Sql = N'ALTER TABLE ' + @TableSchema + '.' + @TableName + N' ADD CONSTRAINT DF_'+@TableName+'_'+@TargetColumnName+' DEFAULT (getutcdate()) FOR '+@TargetColumnName+';'
			EXECUTE sp_executesql @Sql
		END

	FETCH NEXT FROM cur INTO @TableSchema, @TableName, @TargetColumnName, @ColumnDefaultValue
END

CLOSE cur    
DEALLOCATE cur

END

GO
