                CREATE FUNCTION [dbo].[ConvertToDateTimeOffset](@value datetimeoffset)
                RETURNS datetimeoffset AS
                BEGIN
                declare @offset int;

                IF(@value < '1000' OR @value > '9000')
                RETURN @value;
                SET @offset = -360;
IF ((@value > '2016-3-13 2:00:00 AM' AND @value <= '2016-11-6 2:00:00 AM')
OR (@value > '2015-3-8 2:00:00 AM' AND @value <= '2015-11-1 2:00:00 AM')
OR (@value > '2014-3-9 2:00:00 AM' AND @value <= '2014-11-2 2:00:00 AM')
OR (@value > '2013-3-10 2:00:00 AM' AND @value <= '2013-11-3 2:00:00 AM')
OR (@value > '2012-3-11 2:00:00 AM' AND @value <= '2012-11-4 2:00:00 AM')
OR (@value > '2011-3-13 2:00:00 AM' AND @value <= '2011-11-6 2:00:00 AM')
OR (@value > '2010-3-14 2:00:00 AM' AND @value <= '2010-11-7 2:00:00 AM')
OR (@value > '2009-3-8 2:00:00 AM' AND @value <= '2009-11-1 2:00:00 AM')
OR (@value > '2008-3-9 2:00:00 AM' AND @value <= '2008-11-2 2:00:00 AM')
OR (@value > '2007-3-11 2:00:00 AM' AND @value <= '2007-11-4 2:00:00 AM')
OR (@value > '2006-3-12 2:00:00 AM' AND @value <= '2006-11-5 2:00:00 AM')
OR (@value > '2005-3-13 2:00:00 AM' AND @value <= '2005-11-6 2:00:00 AM')
OR (@value > '2004-3-14 2:00:00 AM' AND @value <= '2004-11-7 2:00:00 AM')
OR (@value > '2003-3-9 2:00:00 AM' AND @value <= '2003-11-2 2:00:00 AM')
OR (@value > '2002-3-10 2:00:00 AM' AND @value <= '2002-11-3 2:00:00 AM'))
SET @offset = @offset + 60;

                SET @value = DATEADD(minute, -@offset, @value)
                RETURN SWITCHOFFSET(@value, @offset);
                END;

