GO
Create Function [dbo].[UrlFriendlyString](@UrlValue  VarChar(1000))
Returns VarChar(1000)
AS
Begin

    Declare @KeepValues as varchar(50)
				
	Select @UrlValue  = LTRIM(RTRIM(@UrlValue ))
	Select @UrlValue  = REPLACE(@UrlValue ,'&', 'And')

	-- Replace these chars with dash
	Set @KeepValues = '%[,# _~./\]%'
    While PatIndex(@KeepValues, @UrlValue ) > 0
        Set @UrlValue  = Stuff(@UrlValue , PatIndex(@KeepValues, @UrlValue ), 1, '-')				

	-- Remove anything but these chars
    Set @KeepValues = '%[^a-z0-9-]%'
    While PatIndex(@KeepValues, @UrlValue ) > 0
        Set @UrlValue  = Stuff(@UrlValue , PatIndex(@KeepValues, @UrlValue ), 1, '')

	-- Remove any repeating dashes that may have resulted
	While PatIndex('%--%', @UrlValue ) > 0
		Select @UrlValue = REPLACE(@UrlValue ,'--', '-')
			
    Return @UrlValue 
End

