CREATE TRIGGER [dbo].[AdminUserProfile_SyncUserNameAndEmail_AfterUpdate]
ON [dbo].[AdminUserProfile]
AFTER UPDATE
AS
BEGIN
	IF @@ROWCOUNT = 0 RETURN;
	
	DECLARE @oldUserName as nvarchar(256)
	DECLARE @newUserName as nvarchar(256)
	DECLARE @oldEmail as nvarchar(256)
	DECLARE @newEmail as nvarchar(256)

	SELECT @oldUserName = d.UserName, @newUserName = i.UserName,
			@oldEmail = d.Email, @newEmail = i.Email
		FROM inserted i
			FULL OUTER JOIN deleted d ON i.Id = d.Id;

	IF @oldUserName IS NULL OR @newUserName IS NULL OR @oldEmail IS NULL OR @newEmail IS NULL
		OR @oldUserName = '' OR @newUserName = '' OR @oldEmail = '' OR @newEmail = ''
	RETURN;

	IF @oldUserName = @newUserName AND @oldEmail = @newEmail
	RETURN;

	UPDATE [AspNetUsers]
		SET UserName = 'admin_' + @newUserName,
			Email = 'admin_' + @newEmail
		WHERE UserName = 'admin_' + @oldUserName
END
