CREATE TRIGGER [dbo].[Product_After_Insert]
   ON  [dbo].[Product]
   AFTER INSERT
AS 
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

	-- Update UrlSegment
	UPDATE p SET p.UrlSegment = dbo.UrlFriendlyString(p.UrlSegment) FROM
	  Product p JOIN Inserted i ON p.Id = i.Id

	-- Set Product.IndexStatus to 1 when products and children are created
