-- Description:	Product Update Trigger
-- Update 02/13/15 to handle UrlSegment to strip out unwanted characters
-- =============================================
CREATE TRIGGER [dbo].[Product_After_Update]
   ON  [dbo].[Product]
   AFTER UPDATE
AS 
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

    -- When an update is made where the StyleClass is removed from the Product, null out StyleParentId on StyleChildren
	UPDATE p SET StyleParentId = NULL FROM Product p INNER JOIN INSERTED i ON i.Id = p.StyleParentId 
		INNER JOIN DELETED d ON d.Id = p.StyleParentId WHERE (NOT d.StyleClassId IS NULL) AND i.StyleClassId IS NULL

	-- Set Product.IndexStatus to 1 when products and children are updated, but not if IndexStatus is manually being set to 0
	UPDATE p SET p.IndexStatus = 1 FROM Product p JOIN DELETED d ON 
		p.Id = d.Id OR p.Id = d.StyleParentId
		WHERE d.IndexStatus = 0	

	-- Update UrlSegment
	UPDATE p SET p.UrlSegment = dbo.UrlFriendlyString(p.UrlSegment) FROM
	  Product p JOIN Inserted i ON p.Id = i.Id
END

